import React,{useState,useEffect} from "react";

const Schedule=(props)=>{

    const [userData,setUserData]=useState(props.userData);
    const [values,setValues]=useState({
        start_at:null,
        end_at:null,
        on_date:null,
        class_name:null,
        quiz_details:null,
        subject_name:null,
        sec_name:null,
        session:null,
        p_id:userData?.p_id  || null,
        event:"class",
    });
    const [error,setError]=useState("");
    const [allSections,setAllSections]=useState([]);
    const handleScheduleSubmit=(e)=>{
        e.preventDefault();
        console.log(values)
    }
    useEffect(()=>{
        if(props.allSections && props.allSections.length>0){
            setAllSections(props.allSections);
        }   

    },[props.allSections]);
    return(
        <>
            {
                props.isTeacher && <>
                <p>PLease use hh:mm format for time and YYYY-MM-DD for date</p>
                     <form onSubmit={(e)=>{handleScheduleSubmit(e)}}>
                     <label className="m-3">
                            Lecture Start at:
                            <input onChange={e=>
                                setValues({...values,start_at:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            Lecture End at:
                            <input onChange={e=>
                                setValues({...values,end_at:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            Date:
                            <input onChange={e=>
                                setValues({...values,on_date:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            In class:
                            <input onChange={e=>
                                setValues({...values,class_name:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            Subject:
                            <input onChange={e=>
                                setValues({...values,subject_name:e.target.value})
                            }></input>
                        </label>
                        {
                            values.event=="quiz" && <>
                                 <label className="m-3">
                            Quiz Details:
                            <input onChange={e=>
                                setValues({...values,quiz_details:e.target.value})
                            }></input>
                        </label>
                            </>
                        }
                        <p>Select Event:</p>
                            <input type="radio" id="class" name="event" value="class" onClick={(e)=>{
                                setValues({...values,event:e.target.value})
                            }}/>
                            <label>Class</label>
                            <input type="radio" id="quiz" name="event" value="quiz" onClick={(e)=>{
                                setValues({...values,event:e.target.value})
                            }}/>
                            <label >Quiz</label>
                          <label className="m-3">Select Section
                          <select name="section" id="section" 
                          onChange={(e)=>{
                              alert(JSON.stringify(e.target.value))
                                setValues({...values,sec_name:e.target.value.name,session:e.target.value.session})
                            }}>
                                {allSections.length>0 && allSections.map((sec,i)=>{
                                    return(
                                        <>
                                            {allSections.length==0 && <input disabled={true} placeholder="no section in Database"/>}
                                            {allSections.length>=1 && <option value={{name:sec.sec_name,session:sec.session}}>{sec.sec_name}({sec.session})</option>}
                                        </>
                                    )
                                })}

                                </select></label>
                                <button className="btn btn-warning m-2" style={{width:"fit-content"}} type="submit">Signup</button>

                    </form>

                </>
            }
        </>
    )
}

export default Schedule;
