import React,{useState,useEffect} from "react";

const Schedule=(props)=>{
    const [userData,setUserData]=useState(props.userData);
    const [values,setValues]=useState({
        start_at:null,
        end_at:null,
        on_date:null,
        class_name:null,
        quiz_details:null,
        subject_name:null,
        sec_name:null,
        session:null,
        p_id:null,
        event:"class",
    })
    const [allSections,setAllSections]=useState(props.allSections);
    const handleScheduleSubmit=(e)=>{
        e.preventDefault();
    }

    return(
        <>
            {
                props.isTeacher && <>
                     <form onSubmit={(e)=>{handleScheduleSubmit(e)}}>
                     <label className="m-3">
                            Lecture Start at:
                            <input onChange={e=>
                                setValues({...values,start_at:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            Lecture End at:
                            <input onChange={e=>
                                setValues({...values,end_at:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            Date:
                            <input onChange={e=>
                                setValues({...values,on_date:e.target.value})
                            }></input>
                        </label>
                        <label className="m-3">
                            In class:
                            <input onChange={e=>
                                setValues({...values,class_name:e.target.value})
                            }></input>
                        </label>
                        {
                            values.event=="quiz" && <>
                                 <label className="m-3">
                            Quiz Details:
                            <input onChange={e=>
                                setValues({...values,quiz_details:e.target.value})
                            }></input>
                        </label>
                            </>
                        }
                    </form>

                </>
            }
        </>
    )
}

export default Schedule;
