import React,{useState,useEffect} from "react";
import { getData } from "./api";

const Signup=()=>{
    const [signupAs,setSignupAs]=useState("teacher");
    const [values,setValues]=useState({
        name:"",
        email:"",
        password:"",
    })
    const [teacherSignupValues,setTeacherSignupValues]=useState({
        sec_name:[],
        session:[],
    });
    const [studentSignupValues,setStudentSignupValues]=useState({
        roll_number:"",
        sec_name:""
    })
    const [secList,setSectionList]=useState([])

    useEffect(()=>{
        getSelection();
    },[signupAs])
    const getSection=async ()=>{
        const [result,error]=await getData("/getsection","GET");
        if(error){
            alert("failed to get section list");
        }
        else if(result){
            setSectionList([...result.message])
        }

    }
    return(
        <>
            <div className="signupContainer">
                <div className="topBar w-100 d-flex justify-content-center mt-5">
                    <div className={`btn ${signupAs==="teacher"?"btn-warning":"border border-warning"}`}
                    onClick={()=>{
                        setSignupAs("teacher");
                    }}
                    >Signup As Teacher</div>
                    <div className={`btn ms-2 ${signupAs==="student"?"btn-warning":" border border-warning"}`}
                    onClick={()=>{
                        setSignupAs("student");
                    }}
                    >Signup As Student</div>
                </div>
                <div className="signupPageContent">
                    <form onSubmit={(e)=>{submitSignupForm(e)}}>
                        <label>
                            Name:
                            <input onChange={e=>
                                setValues({...values,name:e.target.value})
                            }></input>
                        </label>
                        <label>
                            Email:
                            <input onChange={e=>
                                setValues({...values,name:e.target.value})
                            }></input>
                        </label>
                        <label>
                            Password:
                            <input onChange={e=>
                                setValues({...values,name:e.target.value})
                            }></input>
                        </label>
                        {
                            signupAs==="teacher" && <>
                                <label>
                            Section Name:
                            <input onChange={e=>{
                                const tempArray=teacherSignupValues.sec_name;
                                tempArray.push(e.target.value)
                                setTeacherSignupValues({...teacherSignupValues,sec_name:tempArray});
                            }
                            }></input>
                        </label>
                        <label>
                            Session:
                            <input onChange={e=>{
                                const tempArray=teacherSignupValues.session;
                                tempArray.push(e.target.value)
                                setTeacherSignupValues({...teacherSignupValues,session:tempArray});
                            }
                            }></input>
                        </label>
                            </>
                        }
                    </form>
                </div>
            </div>
        </>
    )
}

export default Signup;
