import { useParams } from "react-router-dom"
import React,{useState} from "react";
import { Table } from "reactstrap";

const Dashboard=()=>{
    const [currentState,setCurrentState]=useState(AppState.SCHEDULE);
    const [name,setName]=useState("Maaz");
    const [email,setEmail]=useState("2017ee83@student.uet.edu.pk");
    const [section,setSection]=useState(["A","B"]);
    const params=useParams();
    const [timeSlots,setTimeSlots]=useState([
    "12:00Am-01:00Am",
    "02:00Am-03:00Am",
    "03:00Am-04:00Am",
    "04:00Am-05:00Am",
    "05:00Am-06:00Am",
    "06:00Am-07:00Am",
    "07:00Am-08:00Am",
    "08:00Am-09:00Am",
    "09:00Am-10:00Am",
    "10:00Am-11:00Am",
    "11:00Am-12:00Pm",
    "12:00Pm-01:00Pm",
    "02:00Pm-03:00Pm",
    "03:00Pm-04:00Pm",
    "04:00Pm-05:00Pm",
    "05:00Pm-06:00Pm",
    "06:00Pm-07:00Pm",
    "07:00Pm-08:00Pm",
    "08:00Pm-09:00Pm",
    "09:00Pm-10:00Pm",
    "10:00Pm-11:00Pm",
    "11:00Pm-12:00Am",
    ])
    const [lectures,setLectures]=useState({
        Lecture1:["Database(Class 123:A)","Machine Learning(Class 125)","Computer Networks(Class 126)",null,"Database Lab(Class 211)",null,null],
        Lecture2:["Machine Learning(Class 125)","Computer Netwroks Lab(Class 127)","Operation Systems(Class 225)",null,"Database Lab(Class 212)",null,null],
        Lecture3:["Operation Systems(Class 125)","Machine Learning(Class 123:A)","Computer Networks(Class 225)",null,"Database Lab(Class 126)",null,null],
        Lecture4:["Operation Systems(Class 211)","Machine Learning(Class 126)","Computer Networks(Class 123:A)",null,"Database Lab(Class 127)",null,null],
        Lecture5:["Database(Class 211)","Machine Learning(Class 125)","Computer Networks(Class 225)","Machine Learning Lab(Class 126)",null,null,null],
        Lecture6:["Machine Learning(Class 123:A)","Operation Systems(Class 125)","Database(Class 126)",null,null,null,null],

    });
    const [assignments,setAssignments]=useState({
        monday:{
            Assignment:"Database",
            Deadline:"11-00PM",
            Quiz:"Operating Systems Quiz",
            Venue:"Class 123:A"
        },
        tuesday:{
            Assignment:null,
            Deadline:null,
        },
        wednesday:{
            Assignment:null,
            Deadline:null,
        },
        thursday:{
            Assignment:null,
            Deadline:null,
        },
        friday:{
            Assignment:null,
            Deadline:null,
        },
        saturday:{
            Assignment:null,
            Deadline:null,
        },
        sunday:{
            Assignment:"Machine Learning",
            Deadline:"12-00PM"
        }
    })
    return(
        <div className="dashboardContainer d-flex">
            <div className="sideBar p-4" style={{width:"400px",background:"DimGray",height:"100vh"}}>
               <div className="about">
                    <div className="name bold whiteText">
                        {name}
                    </div>
                    <div className="email bold whiteText">
                        {email}
                    </div>

                </div>
                <div className="tabs">
                    <div className="section">
                        <div className="bold whiteText">
                            Schedules:
                        </div>
                        {section.map((sec)=>{
                            return(
                                <>
                                    <li className="singleScetion ps-3 whiteText pointer">Section {sec}</li>
                                </>
                            )
                        })}
                    </div>
                    {
                        parseInt(params.id)===1 &&
                        <>
                            <div className="bold whiteText pointer">
                                Change Schedule
                            </div>
                            <div className="bold whiteText pointer">
                                Add Quiz
                            </div>
                            <div className="bold whiteText pointer">
                                Add Assignment
                            </div>
                        </>
                    }
                </div>
            </div>  
            <div className="rightSide w-100">
                    {currentState===AppState.SCHEDULE && 
                        <>
                            <div>
                                <Table striped bordered>
                                    <thead>
                                    <tr>
                                        <th>Time</th>
                                        <th>Monday</th>
                                        <th>Tuessday</th>
                                        <th>Wednesday</th>
                                        <th>Thursday</th>
                                        <th>Friday</th>
                                        <th>Saturday</th>
                                        <th>Sunday</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <th scope="row">Lecture 1</th>
                                        {lectures.Lecture1.map((lec1,index)=>{
                                            return(
                                                <>
                                                   <th>{lec1 || "Break"}</th>

                                                </>
                                            )
                                        })}
                                    </tr>
                                    <tr>
                                        <th scope="row">Lecture 2</th>
                                        {lectures.Lecture2.map((lec2,index)=>{
                                            return(
                                                <>
                                                   <th>{lec2 || "Break"}</th>

                                                </>
                                            )
                                        })}
                                    </tr>
                                    <tr>
                                        <th scope="row">Lecture 3</th>
                                        {lectures.Lecture3.map((lec3,index)=>{
                                            return(
                                                <>
                                                   <th>{lec3 || "Break"}</th>

                                                </>
                                            )
                                        })}
                                    </tr>
                                    <tr>
                                        <th scope="row">Lecture 4</th>
                                        {lectures.Lecture1.map((lec4,index)=>{
                                            return(
                                                <>
                                                   <th>{lec4 || "Break"}</th>

                                                </>
                                            )
                                        })}
                                    </tr>
                                    <tr>
                                        <th scope="row">Lecture 5</th>
                                        {lectures.Lecture5.map((lec5,index)=>{
                                            return(
                                                <>
                                                   <th>{lec5 || "Break"}</th>

                                                </>
                                            )
                                        })}
                                    </tr>
                                    <tr>
                                        <th scope="row">Lecture 6</th>
                                        {lectures.Lecture6.map((lec6,index)=>{
                                            return(
                                                <>
                                                   <th>{lec6 || "Break"}</th>

                                                </>
                                            )
                                        })}
                                    </tr>

                                    </tbody>
                                </Table>
                            </div>
                        </>
                    }
            </div>  
        </div>
    )
};

export class AppState{
    static SCHEDULE="schedule";
    static CHANGE_SCHEDULE="change_shecdule";
    static ADD_QUIZ="add_quiz";
    static ADD_ASSIGNMENT="add_assignment";
}

export default Dashboard;
